function TabsHidder(){var e=document.querySelectorAll("[data-tab-id]");for(let t=0;t<e.length;t++)e[t].classList.remove("cars__list--active")}function BtnHidder(){var e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].classList.remove("cars__nav__tabs__button--active")}function answer(t){t.target.parentNode.classList.toggle("faq__questions__item--open")}document.addEventListener("DOMContentLoaded",function(){var e=document.querySelectorAll("[data-tab-button]"),a=document.querySelectorAll("[data-faq-question]");for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(t){var e=t.target.dataset.tabButton,e=document.querySelector(`[data-tab-id=${e}]`);TabsHidder(),BtnHidder(),e.classList.add("cars__list--active"),t.target.classList.add("cars__nav__tabs__button--active")});for(let t=0;t<a.length;t++)a[t].addEventListener("click",answer)});